content.mermaid_code:
flowchart TD
    %% 1. Foreign-entity gateway
    A{"Is the entity organized or resident<br>outside the United States?"}
    A -- "No (U.S. entity)" --> NA1["Not an Applicable Person<br>§ 899 does not apply."]
    A -- "Yes (foreign entity)" --> B

    %% 2. Entity-type sorting
    B{"Entity type?"}
        B -- "Private foundation (foreign)" --> AP2["Applicable Person<br>(§ 899(b)(1)(D))"]
    B -- "Trust" --> T
    B -- "Partnership / branch / other Treasury-identified" --> AP3["Applicable Person<br>(§ 899(b)(1)(G))"]
    B -- "Corporation" --> C

    %% Trust path
    T{"Are > 50 % of beneficial interests<br>held by DFC persons?"}
    T -- "Yes" --> AP4["Applicable Person<br>(§ 899(b)(1)(F))"]
    T -- "No" --> NA2["Not an Applicable Person"]

    %% 3. Residency test for corporations
    C{"Is the corporation tax-resident<br>in a listed DFC?"}
    C -- "Yes" --> C1
    C -- "No" --> D

    C1{"Is it a U.S.–owned CFC<br>"}
    C1 -- "Yes" --> NA3["Not an Applicable Person<br>(U.S.–owned CFC carve-out)"]
    C1 -- "No" --> AP5["Applicable Person<br>(§ 899(b)(1)(C))"]

    %% 4. Ownership aggregation test
    D{"Do DFC residents own > 50%<br>(vote or value), directly or indirectly?"}
    D -- "No" --> NA4["Not an Applicable Person"]
    D -- "Yes" --> D1

    D1{"Is the corporation publicly held?"}
    D1 -- "Yes" --> NA5["Applicable Person<br>(public company partial carve-out)"]
    D1 -- "No" --> AP6["Applicable Person<br>(§ 899(b)(1)(E))"]

    %% Styling
    classDef decision fill:#e6f2ff,stroke:#007bff,stroke-width:2px;
    classDef positive fill:#d4edda,stroke:#155724,stroke-width:1px;
    classDef negative fill:#fff3cd,stroke:#856404,stroke-width:1px;

    class A,B,C,C1,D,D1,T decision;
    class AP1,AP2,AP3,AP4,AP5,AP6 positive;
    class NA1,NA2,NA3,NA4,NA5 negative;
